let sliders=document.querySelectorAll(".slide"),slideSection=document.querySelector(".features"),currentSlide="features-slide-1",controlButtons=document.querySelectorAll(".slider-control-button");for(let e=0;e<controlButtons.length;e++)controlButtons[e].addEventListener("click",function(){changeSlider(e)});function changeSlider(e){changeSLiderImage(e),changeCurrentControlButton(e),changeSliderText(e)}function changeCurrentControlButton(e){for(let e=0;e<controlButtons.length;e++)controlButtons[e].classList.remove("current");controlButtons[e].classList.add("current")}function changeSLiderImage(e){e="features-slide-"+(e+1);slideSection.classList.replace(currentSlide,e),currentSlide=e}function changeSliderText(e){for(let e=0;e<sliders.length;e++)sliders[e].classList.remove("slide-current");sliders[e].classList.add("slide-current")}
